module.exports =
{
	create: function(oProps)
	{
		// Create class with constructor.
		if ('__init__' in oProps)
			Class = oProps['__init__'];
		else
			Class = function(){};
		
		// Add methods.
		for (var sName in oProps)
			Class.prototype[sName] = oProps[sName];
		
		return Class;
	}
};